#!/usr/bin/python3# -*- coding: utf-8 -*-# @Time    : 2019-03-10 18:09# @Author  : apollo2mars# @File    : data_convert.py# @Contact : apollo2mars@gmail.com# @Desc    :import numpy as npdef convert_data(input_list):  # 5, 30900    results = []    for model in input_list:        model_result = []        for line in model:            # line = str(line).strip('').strip(']').strip('[')            line = line.strip()            line = line.lstrip('[')            line = line.rstrip(']')            if line.__contains__('\t'):                model_result.append(line.split('\t'))            elif line.__contains__(','):                model_result.append(line.split(','))            elif line.__contains__(' '):                model_result.append(line.split(' '))        results.append(model_result)    results = np.array(results).astype('float64')    results = np.mean(results, axis=0)    return resultsdef convert_string_to_float_list(input_list:list):    return_list = []    for line in input_list:        line = line.strip()        line = line.lstrip('[')        line = line.rstrip(']')        if line.__contains__('\t'):            cut_list = line.split('\t')        elif line.__contains__(', '):            cut_list = line.split(',')        elif line.__contains__(' '):            cut_list = line.split(' ')        float_line_list = [float(tmp) for tmp in cut_list]        return_list.append(float_line_list)    return return_list